import React,{ Component, useState} from 'react';
import './App.css';
import Console from './console';
import {fetchData} from './fecthData';
let ans = 0;
const getData = async() => {
  const api_call = await fetch('https://api.covid19india.org/data.json');
  const response = await api_call.json();
  const length = response.cases_time_series.length;

  ans = response.cases_time_series[length-1].dailyconfirmed;

  // console.log(response.cases_time_series[length-1].dailyconfirmed);
}

const [num,setNum] = useState({
  case: 0,
  death: 0,
});


function App() {
  const [num,setNum] = useState(0); 
  function change(){
    getData();
    setNum(ans);
  }
  function sahil(){
    setInterval(
      ()=>{
        change()
      },1000
    )
  }
  return(
    <>
    <h1>{num.cases} </h1>
    {window.addEventListener('load',sahil)}
    <Console an={ans}/>
    </>
  )

}

export default App;
export {ans};
