import React, {useEffect, useState} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './home.css';

const Covid = () => {
    const [data,setData] = useState([]);

    const getCovidData = async() =>{
        try{
            const api_call = await fetch("https://api.covid19india.org/data.json");
            const main_info = await api_call.json();
            console.log(main_info);
            const lnt =  main_info.cases_time_series.length;
            console.log(lnt);
            setData(main_info.cases_time_series[lnt - 1]);
        }catch(error)
        {
            console.log(error);
        }
    }
    useEffect(() => {
        getCovidData();
    }, []);

    return (
        <>
        {/* <h1>Hello WOrld</h1> */}
        {/* <p> {data.dailyconfirmed} </p> */}

        <div className="container my-4 ">
            <div className="row">
                <div className="col-lg-3 col-md-6 pt-2">
                    <div className="card text-center">
                        <div className="card-body">
                            <div className="card-title"> New Cases </div>
                            <div className="card-text pt-1"> {data.dailyconfirmed} </div>
                        </div>
                    </div>
                </div>
                <div className="col-lg-3 col-md-6 col-sm-12 pt-2">
                    <div className="card text-center">
                        <div className="card-body">
                            <div className="card-title"> New Recovered </div>
                            <div className="card-text"> {data.dailyrecovered} </div>
                        </div>
                    </div>
                </div>
                <div className="col-lg-3 col-md-6 col-sm-12 pt-2">
                    <div className="card text-center">
                        <div className="card-body">
                            <div className="card-title"> Total Confirmed </div>
                            <div className="card-text"> {data.totalconfirmed} </div>
                        </div>
                    </div>
                </div>
                <div className="col-lg-3 col-md-6 col-sm-12 pt-2">
                    <div className="card text-center">
                        <div className="card-body">
                            <div className="card-title"> Total Recovered </div>
                            <div className="card-text"> {data.totalrecovered} </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>

